mapper.select:
select count(0)
from stuff_pack_decompose

	
		id = #{id,jdbcType=INTEGER}
	
	
		AND pack_type_id = (SELECT b.id FROM stuff_pack_type b WHERE b.pack_type_name = #{packTypeName,jdbcType=VARCHAR})
	
	
		AND pack_type_id = #{packTypeId,jdbcType=INTEGER}
	
	
		AND decompose_name LIKE CONCAT('%', #{decomposeName,jdbcType=VARCHAR}, '%')
	
	
		AND decompose_desc LIKE CONCAT('%', #{decomposeDesc,jdbcType=VARCHAR}, '%')
	
	
		AND state = #{state,jdbcType=VARCHAR}

select 

from stuff_pack_decompose a LEFT JOIN stuff_pack b ON a.pack_id = b.id

	
		a.id = #{example.id,jdbcType=INTEGER}
	
	
		AND pack_id =  #{example.packId,jdbcType=INTEGER}
	
	
		AND decompose_name LIKE CONCAT('%', #{example.decomposeName,jdbcType=VARCHAR}, '%')
	
	
		AND decompose_desc LIKE CONCAT('%', #{example.decomposeName,jdbcType=VARCHAR}, '%')
	
	
		AND a.state = #{example.state,jdbcType=VARCHAR}
	
	

ORDER BY a.create_date DESC

	LIMIT #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

select
from stuff_pack_decompose a LEFT JOIN stuff_pack b ON a.pack_id = b.id WHERE a.state = '1' AND ( a.decompose_name LIKE CONCAT('%', #{kw,jdbcType=VARCHAR}, '%') OR a.decompose_desc LIKE CONCAT('%', #{kw,jdbcType=VARCHAR}, '%') ) ORDER BY a.create_date DESC
LIMIT #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

mapper.update:
	UPDATE stuff_pack_decompose SET state = '0', state_date = CURRENT_TIMESTAMP()
	
		id in 
		  
            #{item}
